      x2= 1.18
    }
  }
  let x3 = x*x2
    setResultado(x3)

  }
  return (
    <div className="App">
          <h1>Calculadora react</h1>

        <form onSubmit={handleSubmit}>
        <label>
          Divisa 1:
          <select name="select">
            <option value="value1">Value 1</option>
